			let proto  = null;


			if (url.substr(0, 8) === 'https://') {
				proto = 'https';
				url   = url.substr(8);
			} else if (url.substr(0, 7) === 'http://') {
				proto = 'http';
				url   = url.substr(7);
			}

			let i1 = url.indexOf('?');
